(function() {
	tinymce.create('tinymce.plugins.PastehtmlPlugin', {
		init : function(ed, url) {
			// Register commands
			ed.addCommand('mcePastehtml', function() {
				ed.windowManager.open({
					title: 'Вставить из HTML / Word',
					file : url + '/pastehtml.htm',
					width : 670,
					height : 500,
					inline : 1
				}, {
					plugin_url : url,
				});
			});

			// Register buttons
			ed.addButton('pastehtml', {
				title : 'Вставить из HTML / Word',
				cmd : 'mcePastehtml',
				text : '',
                                icon : 'fa fa fa-paste'                                
			});
		},

		getInfo : function() {
			return {
				longname : 'Очистка HTML',
				author : 'Rincha',
				authorurl : 'http://mir-it.info',
				infourl : '',
				version : tinymce.majorVersion + "." + tinymce.minorVersion
			};
		}
	});

	// Register plugin
	tinymce.PluginManager.add('pastehtml', tinymce.plugins.PastehtmlPlugin);
})();